mapper.select:
SELECT C.CODE_NAME as BOARD_TYPE ,S.BOARD_NUM ,S.BOARD_TITLE ,S.BOARD_COMMENT ,S.TOTAL_CNT FROM ( SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM ASC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT FROM BOARD
WHERE BOARD_TYPE IN
#{type}
) S JOIN COM_CODE C ON S.BOARD_TYPE = C.CODE_ID WHERE NUMROW BETWEEN (#{pageVo.pageNo}-1)*10 + 1 AND (#{pageVo.pageNo})*5

mapper.update:
    	UPDATE BOARD
    	SET
	    	BOARD_TITLE = #{boardTitle},
	    	BOARD_COMMENT = #{boardComment}
    	WHERE BOARD_NUM = #{boardNum}
