mapper.select:
SELECT C.CODE_NAME as CODE_NAME ,S.BOARD_TYPE ,S.BOARD_NUM ,S.BOARD_TITLE ,S.BOARD_COMMENT ,S.TOTAL_CNT FROM ( SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT FROM BOARD
WHERE BOARD_TYPE IN
#{type}
) S JOIN COM_CODE C ON S.BOARD_TYPE = C.CODE_ID WHERE NUMROW BETWEEN (#{pageNo}-1)*10 + 1 AND (#{pageNo})*5 ORDER BY BOARD_NUM DESC

mapper.delete:
DELETE FROM BOARD
WHERE BOARD_NUM = #{boardNum}
AND BOARD_TYPE = #{boardType}

mapper.update:
    	UPDATE BOARD
    	SET
	    	BOARD_TITLE = #{boardTitle},
	    	BOARD_COMMENT = #{boardComment}
    	WHERE BOARD_NUM = #{boardNum}
    	AND BOARD_TYPE = #{boardType}
